body.raw:
{
  "query": "mutation Register($email: String!, $password: String!, $firstName: String!, $lastName: String!) {
  register(email: $email, password: $password, firstName: $firstName, lastName: $lastName) {
    token
    user {
      id
      email
      firstName
      lastName
      role
    }
  }
}",
  "variables": {
    "email": "test@example.com",
    "password": "password123",
    "firstName": "John",
    "lastName": "Doe"
  }
}

{
  "query": "mutation Login($email: String!, $password: String!) {
  login(email: $email, password: $password) {
    token
    user {
      id
      email
      firstName
      lastName
      role
    }
  }
}",
  "variables": {
    "email": "test@example.com",
    "password": "password123"
  }
}

{
  "query": "query {
  me {
    id
    email
    firstName
    lastName
    role
  }
}"
}

{
  "query": "mutation CreateProduct($name: String!, $description: String!, $category: String!, $manufacturer: String!, $modelNumber: String!) {\n  createProduct(name: $name, description: $description, category: $category, manufacturer: $manufacturer, modelNumber: $modelNumber) {\n    id\n    name\n    description\n    category\n    manufacturer\n    modelNumber\n  }\n}",
  "variables": {
    "name": "Test Product",
    "description": "A test product description",
    "category": "Electronics",
    "manufacturer": "Test Manufacturer",
    "modelNumber": "TEST-123"
  }
}

{
  "query": "query {
  products {
    id
    name
    description
    category
    manufacturer
    modelNumber
  }
}"
}

{
  "query": "mutation CreateWarranty($productId: ID!, $purchaseDate: String!, $expirationDate: String!, $warrantyProvider: String!, $warrantyNumber: String!, $coverageDetails: String!) {\n  createWarranty(productId: $productId, purchaseDate: $purchaseDate, expirationDate: $expirationDate, warrantyProvider: $warrantyProvider, warrantyNumber: $warrantyNumber, coverageDetails: $coverageDetails) {\n    id\n    warrantyNumber\n    purchaseDate\n    expirationDate\n    warrantyProvider\n    coverageDetails\n  }\n}",
  "variables": {
    "productId": "{{productId}}",
    "purchaseDate": "2024-03-21",
    "expirationDate": "2025-03-21",
    "warrantyProvider": "Test Provider",
    "warrantyNumber": "WARR-123",
    "coverageDetails": "Full coverage for 1 year"
  }
}

{
  "query": "query {
  warranties {
    id
    warrantyNumber
    purchaseDate
    expirationDate
    warrantyProvider
    coverageDetails
    product {
      name
      manufacturer
    }
  }
}"
}
